package uz.gita.currencyconverter.diimport android.content.Contextimport com.chuckerteam.chucker.api.ChuckerInterceptorimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.android.qualifiers.ApplicationContextimport dagger.hilt.components.SingletonComponentimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryimport retrofit2.createimport uz.gita.currencyconverter.data.source.remote.api.CurrencyApiimport uz.gita.currencyconverter.utils.Constants.BASE_URLimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)class NetworkModule {    @[Provides Singleton]    fun providesClient(@ApplicationContext context: Context): OkHttpClient =        OkHttpClient.Builder().addInterceptor(ChuckerInterceptor.Builder(context).build()).build()    @[Provides Singleton]    fun providesRetrofit(client: OkHttpClient): Retrofit =        Retrofit.Builder()            .addConverterFactory(GsonConverterFactory.create())            .client(client)            .baseUrl(BASE_URL)            .build()    @[Provides Singleton]    fun providesApi(retrofit: Retrofit): CurrencyApi = retrofit.create()}