package uz.gita.currencyconverter.presenter.screens.currencyimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.flow.MutableStateFlowimport kotlinx.coroutines.launchimport javax.inject.Inject@HiltViewModelclass CurrencyViewModel @Inject constructor(    private val direction: CurrencyDirection,) : CurrencyContract.ViewModel, ViewModel() {    override val uiState = MutableStateFlow(CurrencyContract.UIState())    override fun onEventDispatcher(intent: CurrencyContract.Intent) {        when (intent) {            CurrencyContract.Intent.Back -> {                viewModelScope.launch {                    direction.backToMain()                }            }        }    }}