package uz.gita.currencyconverter.presenter.screens.mainimport uz.gita.currencyconverter.domain.models.CurrencyParamimport uz.gita.currencyconverter.presenter.screens.currency.CurrencyScreenimport uz.gita.currencyconverter.utils.navigation.AppNavigatorimport javax.inject.Injectinterface MainDirection {    suspend fun openCurrencyScreen(data1: CurrencyParam, data2: CurrencyParam)}class MainDirectionImpl @Inject constructor(    private val appNavigator: AppNavigator,) : MainDirection {    override suspend fun openCurrencyScreen(data1: CurrencyParam, data2: CurrencyParam) {        appNavigator.openWithSave(CurrencyScreen(data1, data2))    }}